css:
.d-grid{
     border-collapse:collapse;
     background-color:white;
     width: 100%;
     border-radius: 5px;
}
.d-grid-head{
     background-color: #afafaf;
     color: #090909;
}
.d-grid-head tr th{
     padding: 0.75em;
     border-right: solid 1px;
     border-left: solid 1px;
}
.d-grid-head tr th:hover{
     color: #0a0a0a;
     border-top: none;
     border-bottom: none;
}

.d-grid-body tr:hover{
     background-color: #f0f0f0 !important;
}
.d-grid-body tr:nth-child(2n) {
     background-color: #fafafa;
     color: #090909;
}
.d-grid-body tr td{
     padding: 0.5em;
     text-align: center;
     border-bottom: solid 1px #f4f4f4;
     border-left: solid 1px #f4f4f4;
     border-right: solid 1px #f4f4f4;
}

